import React, { useState } from 'react';
import Header from './components/Header';
import ProductionPage from './components/ProductionPage'; // 생산 관리 페이지
import Sidebar from './components/Sidebar'; // 사이드바

function App() {
  const [page, setPage] = useState(null); // 현재 표시할 페이지

  const handlePageChange = (pageName) => {
    switch (pageName) {
      case 'production':
        setPage(<ProductionPage />);
        break;
      // 다른 페이지들을 추가할 수 있음
      default:
        setPage(null);
    }
  };

  return (
    <div>
      <Header onChangePage={handlePageChange} />
      <div className="container">
        <Sidebar />
        <div className="main-content">
          {page ? page : <p>메인 대시보드입니다. 페이지를 선택하세요.</p>}
        </div>
      </div>
    </div>
  );
}

export default App;
