import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import ProductionSidebar from './components/ProductionSidebar';
import ProductionPage from './components/ProductionPage';  // 생산 관리 페이지

function App() {
    return (
        <Router>
            <Header />
            <div className="custom-container" style={{ display: 'flex' }}>
                {/* 모든 페이지에서 사이드바를 렌더링하지만, 페이지에 따라 자동으로 열리고 닫힘 */}
                <ProductionSidebar />
                <div className="main-content">
                    <Routes>
                        {/* 메인 페이지에서는 사이드바가 닫힘 */}
                        <Route path="/" element={<div>Main Page Content</div>} />

                        {/* 생산 관리 페이지 */}
                        <Route path="/production" element={<ProductionPage />} />

                        {/* 추가적인 페이지 경로들 */}
                        <Route path="/input" element={<div>InputForm</div>} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

export default App;
