import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Header from './components/Header';
import ProductionSidebar from './components/ProductionSidebar';
import ProductionPage from './components/ProductionPage';
import MainPage from './components/MainPage'; // 메인 페이지 컴포넌트

function App() {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false); // 사이드바 열림/닫힘 상태 관리

    // 사이드바 열기/닫기 함수
    const toggleSidebar = (show) => {
        setIsSidebarOpen(show);
    };

    return (
        <Router>
            <Header />
            <div className="custom-container">
                {/* 메인 페이지에서는 사이드바 없이 렌더링 */}
                <Routes>
                    <Route path="/" element={
                        <>
                            {/* 메인 페이지로 이동하면 사이드바 닫힘 */}
                            <MainPage toggleSidebar={toggleSidebar} />
                        </>
                    } />

                    {/* 생산 관리 페이지에서는 사이드바 열림 */}
                    <Route path="/production" element={
                        <>
                            <ProductionSidebar isSidebarOpen={isSidebarOpen} />
                            <ProductionPage />
                        </>
                    } />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
