import React from 'react';

function ProductionPage() {
  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);

    fetch('/api/production', {
      method: 'POST',
      body: formData,
    })
      .then(response => response.json())
      .then(data => {
        console.log('Success:', data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <div>
      <h1>생산 실적 등록</h1>
      <form onSubmit={handleSubmit}>
        <label>작업 일자: <input type="date" name="production-date" required /></label>
        <label>작업지시번호: <input type="text" name="production-calculation" required /></label>
        <label>품목 코드: <input type="text" name="production-period" required /></label>
        <label>품목명: <input type="text" name="base-item" required /></label>
        <label>수량: <input type="text" name="status" required /></label>
        <label>불량률: <input type="text" name="mrp-calculation" required /></label>
        <button type="submit">등록</button>
      </form>
    </div>
  );
}

export default ProductionPage;
