import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto'; 
import './ProductionPage9.css'; 

function ProductionPage9() {
    const [chartData, setChartData] = useState({});
    const [isDataLoaded, setIsDataLoaded] = useState(false);

    useEffect(() => {
        const fetchData = () => {
            // 날짜와 데이터를 10일치로 확장
            const dummyData = {
                labels: ["2024-10-01", "2024-10-02", "2024-10-03", "2024-10-04", "2024-10-05", "2024-10-06", "2024-10-07", "2024-10-08", "2024-10-09", "2024-10-10"],
                datasets: [
                    {
                        label: '생산된 양 (단위: 개)',
                        data: [40, 55, 60, 70, 85, 90, 100, 110, 120, 130],
                        borderColor: 'rgba(75,192,192,1)',
                        backgroundColor: 'rgba(75,192,192,0.2)',
                        fill: true,
                    },
                    {
                        label: '불량 갯수',
                        data: [5, 3, 6, 2, 5, 7, 8, 5, 4, 6],
                        borderColor: 'rgba(255,99,132,1)',
                        backgroundColor: 'rgba(255,99,132,0.2)',
                        fill: true,
                    }
                ]
            };
            setChartData(dummyData);
            setIsDataLoaded(true);
        };

        fetchData();
    }, []);

    return (
        <div className="chart-container" style={{ marginLeft: '50px' }}> {/* 차트를 오른쪽으로 조금 이동 */}
            {isDataLoaded ? (
                <Line data={chartData} options={{
                    scales: {
                        y: {
                            beginAtZero: true
                        }
                    }
                }} />
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
}

export default ProductionPage9;
