import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto'; // Chart.js에서 자동으로 필요한 요소를 불러옴
import './ProductionPage.css'; 

function ProductionPage9() {
    const [chartData, setChartData] = useState({}); // 그래프 데이터를 관리하는 state
    const [isDataLoaded, setIsDataLoaded] = useState(false);

    useEffect(() => {
        // 데이터를 불러오는 부분 (추후에 비동기 통신으로 데이터를 불러올 수 있습니다)
        const fetchData = () => {
            const dummyData = {
                labels: ["2024-10-01", "2024-10-02", "2024-10-03", "2024-10-04", "2024-10-05"],
                datasets: [
                    {
                        label: '생산된 양 (단위: 개)',
                        data: [40, 55, 60, 70, 85],
                        borderColor: 'rgba(75,192,192,1)',
                        backgroundColor: 'rgba(75,192,192,0.2)',
                        tension: 0.4, // 꺾은선의 곡률 조정
                    }
                ]
            };
            setChartData(dummyData);
            setIsDataLoaded(true);
        };

        fetchData();
    }, []);

    return (
        <div className="production-page9">
            <h1>날짜별 생산 실적율</h1>
            {isDataLoaded ? (
                <div className="chart-container">
                    <Line 
                        data={chartData}
                        options={{
                            scales: {
                                y: {
                                    beginAtZero: true,
                                    title: {
                                        display: true,
                                        text: '생산된 양 (개)',
                                    }
                                },
                                x: {
                                    title: {
                                        display: true,
                                        text: '날짜',
                                    }
                                }
                            }
                        }}
                    />
                </div>
            ) : (
                <p>데이터를 불러오는 중입니다...</p>
            )}
        </div>
    );
}

export default ProductionPage9;
